	const EMPTY_COMMENT_START_RX = /^\/\*!?[ \r\n\t\f]*$/;
	// Used to test whether a bit of text ends an empty comment.
	const EMPTY_COMMENT_END_RX = /^[ \r\n\t\f]*\*\//;
